<?php
	header('Content-Type: text/javascript');
	session_start(); 
	require_once( "./auth.php" );
	require_once( "./atlas.php" );
	require_once( "./libs/hybridauth-2.2.2/hybridauth/Hybrid/Auth.php" );

	$domains = array();
	$providers = array();
	$contacts = array();
	try{
		$hybridauth = new Hybrid_Auth( $config );
		$connected_adapters_list = $hybridauth->getConnectedProviders(); 
		//
		// get host default configuration
		$keys = array_keys( $hosts );
		foreach( $keys as $k ) {
			$domains[] = array( 'name' => $hosts[$k]['name'], 'api' => $hosts[$k]['api'], 'domain' => $k, 'storage' => false );
		}
		// get user defined domains from cookies
		if ( isset( $_COOKIE['ATLAS_DOMAINS'] ) ){
			$udd = json_decode( $_COOKIE['ATLAS_DOMAINS'] );
			if ( $udd ) {
				foreach( $udd as $d ) {
					$domains[] = array( 'name' => $d->name, 'api' => $d->api, 'domain' => $d->domain, 'storage' => false );
				}
			}
		}

		//
		foreach( $config['providers'] as $ck => $cv ) {
			$provider = array( 'id' => $ck, 'enabled' => $cv['enabled'], 'img' => $cv['img'], 'photo' => '' );
			if ( $cv['enabled'] ) {
				if ( in_array( $ck, $connected_adapters_list ) ) {
					try {
						//
						$adapter = $hybridauth->authenticate( $ck );  
						$user_profile = $adapter->getUserProfile();
						$provider['photo'] = $user_profile->photoURL;
						if ( !$provider['photo'] ) {
							$provider['photo'] = "data:image/png;base64,";
						}

						//
						if ( $cv['storage'] ) {
							$domains[] = array( 'name' => $ck, 'api' => '', 'domain' => '', 'storage' => $cv['storage'] );
						}

/*
						$user_contacts = $adapter->getUserContacts();
						foreach( $user_contacts as $contact ) {
							$contacts[$ck][] = array( 'id' => $contact->identifier, 'name' => $contact->displayName, 'photo' => $contact->photoURL, 'email' => $contact->email );
						}
*/
					}
					catch( Exception $e ){
					}
				}
			}
			$providers[] = $provider;
		}
	}
	catch( Exception $e ){
	}

?>
var domains = <?php echo json_encode( $domains );?>;
var providers = <?php echo json_encode( $providers );?>;
var contacts = <?php echo json_encode( $contacts );?>;
